INSERT INTO `daotao` VALUES (1,'CHĂM SÓC DA','<p>Ng&agrave;nh chăm s&oacute;c da&nbsp;&ndash; spa lu&ocirc;n l&agrave; ng&agrave;nh ngh&ecirc; HOT nhất, thu h&uacute;t số lượng học vi&ecirc;n lớn nhất bởi cơ hội việc l&agrave;m ổn định với mức thu nhập cao.</p>\r\n','1_cham-soc-da.png'),(2,'PHUN XĂM','<p><a href=\"http://vienthammyevaxinh.com/page/chuyen-nghanh-dao-tao-phun-sam#\">Học phun xăm thẩm mỹ</a>&nbsp;cho bạn cơ hội trở th&agrave;nh những nghệ nh&acirc;n phun xăm h&agrave;ng đầu với tay nghề đi&ecirc;u luyện, vừa c&oacute; thể &ldquo;hốt bạc&rdquo;, vừa c&oacute; thể thỏa niềm đam m&ecirc; l&agrave;m đẹp.</p>\r\n','2_phun-xam.png'),(3,'TK TẠO MẪU TÓC','<p>Nghề&nbsp;thiết kế tạo mẫu t&oacute;c&nbsp;kh&ocirc;ng bao giờ l&agrave; lỗi mốt trong x&atilde; hội hiện đại. H&atilde;y nắm bắt cơ hội được đ&agrave;o tạo th&agrave;nh những nh&agrave; thiết kế tạo mẫu t&oacute;c chuy&ecirc;n nghiệp trong tương lai.</p>\r\n','3_tao-mau-toc.png'),(4,'TRANG ĐIỂM','<p>Nghề trang điểm&nbsp;lu&ocirc;n đứng đầu danh s&aacute;ch được c&aacute;c bạn trẻ lựa chọn lập nghiệp với niềm đam m&ecirc; l&agrave;m đẹp cho mọi người v&agrave; cho ch&iacute;nh bản th&acirc;n c&aacute;c bạn.</p>\r\n','4_trang-diem.png'),(5,'QUẢN LÝ SPA','<p>Bạn mơ ước l&agrave;m chủ một spa cao cấp của ri&ecirc;ng m&igrave;nh? C&oacute; đam m&ecirc;, d&aacute;m ước mơ, ch&uacute;ng t&ocirc;i sẽ gi&uacute;p bạn ho&agrave;n thiện kiến thức v&agrave; kỹ năng&nbsp;<a href=\"http://vienthammyevaxinh.com/page/chuyen-nghang-dao-tao-spa\">quản l&yacute; spa</a>&nbsp;chuy&ecirc;n nghiệp.</p>\r\n','5_quan-ly-spa.png'),(6,'VẼ MÓNG','<p>Nghề nail&nbsp;l&agrave; một ng&agrave;nh nghề mới đang được nhiều bạn trẻ lựa chọn để khởi nghiệp, để thỏa sức s&aacute;ng tạo v&agrave; trổ t&agrave;i kh&eacute;o l&eacute;o với những bộ m&oacute;ng xinh đẹp.</p>\r\n','6_ve-mong.png'),(12,'PHUN XĂM HÀN QUỐC','<p><a href=\"http://vienthammyevaxinh.com/page/khoa-hoc-phun-xam-phun-theu-th%E1%BA%A8m-my#\">Học phun xăm thẩm mỹ</a><span style=\"font-family:source sans pro,sans-serif\">&nbsp;cho bạn cơ hội trở th&agrave;nh những nghệ nh&acirc;n phun xăm h&agrave;ng đầu với tay nghề đi&ecirc;u luyện, vừa c&oacute; thể &ldquo;hốt bạc&rdquo;, vừa c&oacute; thể thỏa niềm đam m&ecirc; l&agrave;m đẹp.</span></p>\r\n','12_phun-theu.png');
