INSERT INTO `explain_data` VALUES (4,43,'Điểm nhấn của khóa học','<p>&nbsp;Lớp học được thiết kế theo m&ocirc; h&igrave;nh 1 thầy &ndash; 10 tr&ograve;</p>\r\n\r\n<p>Học vi&ecirc;n được tặng gi&aacute;o tr&igrave;nh học chăm s&oacute;c da &ndash; spa đạt chuẩn quốc tế</p>\r\n\r\n<p>Cố vấn chuy&ecirc;n m&ocirc;n l&agrave; c&aacute;c chuy&ecirc;n gia, b&aacute;c sĩ da liễu h&agrave;ng đầu</p>\r\n\r\n<p>Học vi&ecirc;n được hỗ trợ to&agrave;n bộ mỹ phẩm, vật liệu v&agrave; m&aacute;y m&oacute;c trong qu&aacute; tr&igrave;nh học</p>\r\n\r\n<p>Thực h&agrave;nh c&aacute;c kỹ năng tay nghề với hệ thống c&ocirc;ng nghệ cao trong ng&agrave;nh thẩm mỹ</p>\r\n',3),(5,43,'CHI TIẾT KHÓA HỌC PHUN XĂM THẨM MỸ','<p>B&agrave;i 1 : DỤNG CỤ CẦN THIẾT KHI PHUN TH&Ecirc;U CH&Acirc;N M&Agrave;Y</p>\r\n\r\n<p>B&agrave;i 2 : C&Aacute;CH LỰA CHỌN, BẢO QUẢN MẤY PHUN M&Agrave;U</p>\r\n\r\n<p>B&agrave;i 3 : C&Aacute;CH LẮP R&Aacute;P MẤY PHUN XĂM</p>\r\n\r\n<p>B&agrave;i 4 : NHỮNG Y&Ecirc;U CẦU V&Agrave; NGUY&Ecirc;N TẮC CƠ BẢN KHI PHUN M&Agrave;U THẨM MỸ</p>\r\n\r\n<p>B&agrave;i 5 : C&Aacute;C H&Igrave;NH DẶNG KHU&Ocirc;N MẶT</p>\r\n\r\n<p>B&agrave;i 6 : C&Aacute;C H&Igrave;NH DẠNG CH&Acirc;N M&Agrave;Y</p>\r\n\r\n<p>B&agrave;i 7 : QUY TR&Igrave;NH TIẾN H&Agrave;NH PHUN M&Agrave;U THẨM MỸ</p>\r\n\r\n<p>B&agrave;i 8 : KỸ THUẬT PHUN CH&Acirc;N M&Agrave;Y</p>\r\n\r\n<p>B&agrave;i 9 : C&Aacute;CH PHA M&Agrave;U MỰC CHO CH&Acirc;N M&Agrave;Y</p>\r\n\r\n<p>B&agrave;i 10 : KỸ THUẬT X&Oacute;A CH&Acirc;N M&Agrave;Y</p>\r\n\r\n<p>B&agrave;i 11 : NHỮNG LỖI KỸ THUẬT THƯỜNG GẶP KHI PHUN TH&Ecirc;U CH&Acirc;N M&Agrave;Y</p>\r\n\r\n<p>B&agrave;i 12 : TH&Ecirc;U CH&Acirc;N M&Agrave;Y</p>\r\n',3),(9,43,'LÌ XÌ ĐI HỌC PHUN XĂM ĐẦU NĂM 2017 ','<p>TĂNG NGAY M&Aacute;Y PHUN KHI ĐĂNG K&Yacute; TRONG TH&Aacute;NG 3&nbsp;N&Agrave;Y</p>\r\n\r\n<p>TẶNG NGAY 1 KH&Oacute;A HỌC NỐI MI KHI ĐĂNG K&Yacute; TRONG TH&Aacute;NG 2 N&Agrave;Y</p>\r\n\r\n<p>TĂNG NGAY Ở TẠI HỌC VIỆN MIỄN PH&Iacute; CHỖ Ở &nbsp;TROG TH&Aacute;NG 2 N